import React from 'react'
import './Main.css'
import SearchBarBox from './SearchBarBox'

function MainPage() {
    let query = window.location.search.split('=')[1]
    const searchInput = document.getElementById("query");
      const autocompleteContainer = document.getElementById("autocomplete");
      const suggestionList = document.querySelector(".search__suggestions__list");
      // make .search__suggestions visibility hidden 
      // so that it doesn't show up when there are no suggestions
      const autocompleteContainer1 = document.getElementById("auto1");
      autocompleteContainer1.style.visibility = "hidden";


      searchInput.addEventListener("input", async function(event) {
        const query = event.target.value;

        if (!query) {
          suggestionList.innerHTML = "";
          console.log("no query");
          return;
        }

        const response = await fetch(`https://cors-anywhere.binayak123.repl.co/search.brave.com/api/suggest?q=${query}`);
        console.log(response);
        const suggestions = await response.json();

        console.log(suggestions);

        // the suggestion is in ['e', Array(8)] form so we need to loop through it
        const suggestionsList = suggestions[1].map(suggestion => {
          return `<li class="search__suggestions__list__result">${suggestion}</li>`;
        });
        console.log(suggestionsList);
        // show autocompleteContainer1
        autocompleteContainer1.style.visibility = "visible";
        // now add the suggestions to the DOM
        suggestionList.innerHTML = suggestionsList.join("");

        // add event listener to each suggestion
        const suggestionItems = document.querySelectorAll(".search__suggestions__list__result");
        suggestionItems.forEach(item => {
          item.addEventListener("click", function(event) {
            searchInput.value = event.target.innerText;
            suggestionList.innerHTML = "";
          });
        });
    });

  return (
    <div className='mainpage'>
        <div id="main">
            <div className="container-flex">
                <div className="row">
                    <h1 className="heading">
                        <img src={process.env.PUBLIC_URL +'/images/logo.png'} style={{width: 90,height:'auto',marginRight: 6,marginBottom: 3}}/>
                        LuMiFy
                    </h1>

                    <div className="searchform-container">
                        <center>
                            <SearchBarBox />
                        </center>
                    </div>
                </div>
                <br/>
            </div>
        </div>

    </div>
  )
}

export default MainPage
